mapper.select:
SELECT r.record_board_code ,r.member_id ,r.record_board_name ,r.record_board_sub_name ,r.content ,r.plan_status_code ,r.start_date ,r.end_date ,r.reg_date ,r.like_count ,r.views ,f.file_path FROM tb_record_board AS r INNER JOIN tb_member AS m ON r.member_id = m.member_id LEFT JOIN tb_file_control AS fc ON r.record_board_code = fc.reference_code LEFT JOIN tb_file AS f ON fc.file_idx = f.file_idx
h.member_id = #{memberId}
AND ${searchKey} LIKE CONCAT('%', #{searchValue}, '%')
ORDER BY r.record_board_code;

mapper.insert:
	
		SELECT sf_new_record_board_code() as recordBoardCode
	
	INSERT INTO  tb_record_board
	(   record_board_code
	   ,member_id
	   ,record_board_name
	   ,record_board_sub_name
	   ,content  
	   ,plan_status_code    
	   ,start_date   
	   ,end_date
	   ,reg_date    
	   ,like_count    
	   ,views    
	) VALUES (
	    #{recordBoardCode}
	   ,#{memberId}
	   ,#{recordBoardName}
	   ,#{recordBoardSubName}
	   ,#{content}
	   ,#{planStatusCode}
	   ,#{startDate}
	   ,#{endDate}
	   ,CURDATE()
	   ,#{likeCount}
	   ,#{views}
	);

   	  
			SELECT sf_new_record_board_feed_code() as feedCode
	  
      INSERT INTO tb_record_board_feed
      (   feed_code
         ,record_board_code
         ,feed_title
         ,feed_idx 
         ,feed_content 
         ,feed_time
         ,feed_reg_date   
         ,feed_plan_days   
      ) VALUES (
          #{feedCode}
         ,#{recordBoardCode}
         ,#{feedTitle}
         ,#{feedIdx}
         ,#{feedContent}
         ,#{feedTime}
         ,CURDATE()
         ,#{feedPlanDays}
      );

INSERT INTO tb_record_board_comment
(   comment_num
   ,record_board_code
   ,content
   ,member_id  
   ,reg_date      
) VALUES (
    sf_new_feed_comment_code()
   ,#{recordBoardCode}
   ,#{content}
   ,#{memberId}
   ,Now()
);
