mapper.insert:
	INSERT INTO  tb_record_board
	(   record_board_code
	   ,member_id
	   ,record_board_name
	   ,content  
	   ,plan_status_code    
	   ,image
	   ,start_date   
	   ,end_date
	   ,reg_date    
	   ,like_count    
	   ,views    
	) VALUES (
	   sf_new_record_board_code()
	   ,#{memberId}
	   ,#{recordBoardName}
	   ,#{content}
	   ,#{planStatusCode}
	   ,#{image}
	   ,#{startDate}
	   ,#{endDate}
	   ,CURDATE()
	   ,#{likeCount}
	   ,#{views}
	);

